mapper.select:
select u.nombre as u_nombre, re.nombre as re_nombre, re.descripcion as re_descripcion, re.ubicacion as re_ubicacion, r.fecha, r.horaini, r.horafin
From usuarios u JOIN reservas r ON r.carnet = u.carnet JOIN recursos re ON re.id_recurso = r.recurso
WHERE u.correo = #{email}
ORDER BY r.horaini;
